url_options.body_graphql_query:
query Viewer {
    viewer {
        zones(filter: { zoneTag: "$zone_id" }) {
            zoneTag
            dnsAnalyticsAdaptiveGroups(
                limit: 5000
                filter: {
                    datetimeHour_geq: "${__from:date:YYYY-MM-DDTHH:mm:ssZ}"
                    datetimeHour_leq: "${__to:date:YYYY-MM-DDTHH:mm:ssZ}"
                }
            ) {
                count
                avg {
                    processingTimeUs
                    sampleInterval
                }
            }
        }
    }
}

